var dewP = msg.data.current_observation.dewpoint_c ;        //C
var solRadiation = Math.round(msg.data.current_observation.solarradiation) ;    //
var uvRadiation = msg.data.current_observation.UV ;         //0-11

//cut the % from the end
hum = hum.substring(0, hum.length - 1);
//kph to m/s * 100
wSpeed = wSpeed / 3.6 ;

