var ethJsUtil = global.get('ethereumjs') ;
var Web3 = global.get('web3') ;
var web3 = new Web3(new Web3.providers.HttpProvider("http://localhost:8545"));
if (msg.payload != null) {
    if (msg.payload.address != null && msg.payload.signedData != null) {
        var urlTable = "sensor" + msg.payload.sensor ;
        var initialData = "0x9dd2c369a187b4e6b9c402f030e50743e619301ea62aa4c0737d4ef7e10a3d49" ;
        //var initialData = msg.payload.address ;
        var address = msg.payload.address ;
        var sig = msg.payload.signedData ;  //the signed data

        //console.log(address + ", " + sig) ;

        r = sig.slice(0, 66) ;
        s = '0x' + sig.slice(66, 130) ;
        v = '0x' + sig.slice(130, 132) ;
        v = web3.toDecimal(v) ;
        initialData = ethJsUtil.toBuffer(initialData)
        pub     = ethJsUtil.ecrecover(initialData, v, r, s);
        addrBuf = ethJsUtil.pubToAddress(pub);
        addr    = ethJsUtil.bufferToHex(addrBuf);

        //console.log(address + ", " + addr) ;
        //if address = addr then the user is who he says to be
        if (address == addr) {
            msg.addressForApiKey = address ;
            msg.topic = "address verified" ;
            return msg ;
        }
    }
}
msg.topic = "" ;  //if not successfully verified  
//console.log(msg.topic) ;                                                  //send nothing
return msg ;

