var account ;

window.addEventListener('load', function() {
	// Checking if Web3 has been injected by the browser (Mist/MetaMask)
	if (typeof web3 !== 'undefined') {
		// Use Mist/MetaMask's provider
		console.log("Use Mist/MetaMask's provider") ;
		window.web3 = new Web3(web3.currentProvider);
		console.log(web3.currentProvider) ;
		console.log(window.web3.currentProvider) ;
	} else {
		console.log('No web3? You should consider trying MetaMask!')
		// fallback - use your fallback strategy (local node / hosted node + in-dapp id mgmt / fail)
		window.web3 = new Web3(new Web3.providers.HttpProvider("http://localhost:8545"));
		web3.eth.defaultAccount = web3.eth.accounts[0] ;
	}
	web3.version.getNetwork((err, netId) => {
	switch (netId) {
		case "1":
		console.log('This is mainnet')
		break
		case "2":
		console.log('This is the deprecated Morden test network.')
		break
		case "3":
		console.log('This is the ropsten test network.')
		break
		default:
		console.log('This is an unknown network.')
	}
	})

	account = window.web3.eth.accounts[0];
	var accountInterval = setInterval(function() {
		//console.log("checking") ;
		if (window.web3.eth.accounts[0] !== account) {
		account = window.web3.eth.accounts[0];
		console.log("new account" + account) ;
		start() ;
	}
	}, 50);
	start() ;
});

function start() {
    document.getElementById("ifWeb3NotInjected").style.visibility = "hidden" ;
    document.getElementById("ifWeb3Injected").style.visibility = "visible" ;
}

function searchAll() {
    document.getElementById("searchButton").innerHTML = "Show Search" ;
    document.getElementById("SearchSensors").style.visibility = "collapse" ;
    var url = "/sensors/getMySensors?buyer=" + account ;
    $.get(url, function(data, status){
        document.getElementById("listSensors").innerHTML = data ;
    });
}

