function createSensor() {
    //address seller1, uint8 type1, uint price1, uint32 startTime1, 
    //uint16 frequency1, int32 longitude1, int32 latitude1, string url1
    var coordMult = 1000000 ;
    var seller = document.getElementById("seller").value ;
    var type = parseInt(document.getElementById("sensorType").value) ;
    var price = parseFloat(document.getElementById("price").value) ;
    var frequency = parseFloat(document.getElementById("frequency").value) ;
    //var startTime = parseInt(document.getElementById("startTime").value) ;
    var startTime = document.getElementById("startTime").value ;
    startTime = new Date(startTime).getTime()/1000 ;
    var latitude = parseFloat(document.getElementById("latitude").value) * coordMult ;
    var longtitude = parseFloat(document.getElementById("longtitude").value) * coordMult ;
    var url = document.getElementById("url").value ;
    /*console.log(seller, typeof(seller)) ;
    console.log(type, typeof(type)) ;
    console.log(price, typeof(price)) ;
    console.log(frequency, typeof(frequency)) ;
    console.log(startTime, typeof(startTime)) ;
    console.log(latitude, typeof(latitude)) ;
    console.log(longtitude, typeof(longtitude)) ;
    console.log(url, typeof(url)) ;*/

    if (!seller || !type|| !price || !frequency || !startTime || !latitude || !longtitude || !url) {
        alert("All fields are mandatory.") ;
        return ;
    }
    if(!Number.isInteger(frequency)) {
        alert("Frequency must be an integer") ;
        return ;
    }
    if(frequency == 0 ) {
        alert("Frequency must not be zero.") ;
        return ;
    }
    if(startTime < 0) {
        alert("First measurement at must not be negative.") ;
        return ;
    }
    if(latitude<-180*coordMult || latitude>180*coordMult || longtitude<-90*coordMult || longtitude>90*coordMult) {
        alert("Invalid coordinates.") ;
        return ;
    }
    //nothing is a float
    price = window.web3.toWei(price,"ether") ;
    BrokerContract.createSensor(seller, type, price, startTime, frequency, latitude, longtitude, url, {gas: 300000}, (err,res) =>{
        if(!err) {
            console.log(res) ;
            alert("Sensor created successfully. TxHash: " + res) ;
        }
        else {
            alert("An error has occurred, please try later.") ;
        }
    }) ;
}

