function search() {
    document.getElementById("searchButton").innerHTML = "Show Search" ;
    document.getElementById("SearchTxns").style.visibility = "collapse" ;
    var url = "/txns/getTxns" ;
    txhash = document.getElementById("txhash").value ;
    blockNo = document.getElementById("blockNo").value ;
    contract = document.getElementById("contract").value ;
    var isFirst = true ;
    var symbol ;
    if (txhash != "") {
        symbol = (isFirst) ? "?" : "&" ;
        url += symbol + "txhash=" + txhash ;
        isFirst = false ;
    }
    if (blockNo != "") {
        symbol = (isFirst) ? "?" : "&" ;
        url += symbol + "blockNo=" + blockNo ;
        isFirst = false ;
    }
    if (contract != "") {
        symbol = (isFirst) ? "?" : "&" ;
        url += symbol + "contract=" + contract ;
        isFirst = false ;
    }
    //console.log(url) ;
    $.get(url, function(data, status){
        document.getElementById("listTransactions").innerHTML = data ;
    });
}

function showSearch() {
    if (document.getElementById("searchButton").innerHTML == "Show Search") {
        document.getElementById("searchButton").innerHTML = "Hide Search" ;
        document.getElementById("SearchTxns").style.visibility = "visible" ;
    }
    else {
        document.getElementById("searchButton").innerHTML = "Show Search" ;
        document.getElementById("SearchTxns").style.visibility = "collapse" ;
    }
}

