var query = "SELECT * " +
    "FROM txns " ;


var isFirst = true ;
var txhash = msg.payload.txhash ;
var blockNo = msg.payload.blockNo ;
var contract = msg.payload.contract ;

var conditions = "" ;
if (txhash != null) {
    symbol = (isFirst) ? "WHERE " : "AND " ;
    conditions += symbol + " transactionHash='" + txhash + "' "  ;
    isFirst = false ;
}
if (blockNo != null) {
    symbol = (isFirst) ? "WHERE " : "AND " ;
    conditions += symbol + " blockNumber=" + blockNo + " "  ;
    isFirst = false ;
}
if (contract != null && contract != 0) {
    symbol = (isFirst) ? "WHERE " : "AND " ;
    conditions += symbol + " address='" + contract + "' "  ;
    isFirst = false ;
}

query += conditions ;
msg.topic = query ;
console.log(msg.topic) ;
return msg;

var priceMult = 1000000000000000000 ;

var table = "<thead class=\"bg-light\">" +
                "<tr><th>Log Index</th><th>Tx Index</th><th>Tx Hash</th>" +
                "<th>Block Hash</th><th>Block Number</th>" +
                "<th>Contract Address (Contract Name)</th><th>Type</th>" +
                "<th>Event</th></tr>"+
            "</thead>" ;
