if (!msg.payload) {
   table += "<tr><td>Error, something happened</td></tr>" ;
} else {
    var i, len = msg.payload.length, contract = ""  ;
    if (len > 0) {
        for(i in msg.payload) {
            contract = "" ;
            if (msg.payload[i].address == "0x67dbda62a1716679c9ca1fbfab3fcecd36b4dcc5")
                contract = " (NtuaToken)" ;
            else if(msg.payload[i].address == "0x3162447df38985e24d38cf3b67181a82b61de56c")
                contract = " (Broker)" ;
            table += "<tr><td>" + msg.payload[i].logIndex + "</td>" +
                    "<td>" + msg.payload[i].transactionIndex + "</td>" +
                    "<td>" + msg.payload[i].transactionHash + "</td>" +
                    "<td>" + msg.payload[i].blockHash + "</td>" +
                    "<td>" + msg.payload[i].blockNumber + "</td>" +
                    "<td>" + msg.payload[i].address + contract + "</td>" +
                    "<td>" + msg.payload[i].type + "</td>" +
                    "<td>" + msg.payload[i].event + "</td></tr>" ;
        }
    }
    else {
        table = "<tr><td>No transactions available.</td></tr>" ;
    }
}

msg.payload.table = table ;
